import React from "react";
import { Button, Col, Form, Row, Table } from "react-bootstrap";
import ZavrsniAxios from "../../apis/ZavrsniAxios";

class Class extends React.Component {

    constructor(props) {
        super(props);

        const search = {
            reprezentacijaAId: null,
            reprezentacijaBId: null,
        }

        this.state = {
            utakmice: [],
            reprezentacije: [],
            igraci: [],
            number: 0,
            pageNo: 0,
            totalPages: 1,
            search: search
        }
    }

    componentDidMount() {
        this.getUtakmice(0);
        this.getreprezentacije();
        this.getIgraci();
    }

    getUtakmice(newPageNo) {
        let config = {
            params: {
                reprezentacijaAId: this.state.search.reprezentacijaAId,
                reprezentacijaBId: this.state.search.reprezentacijaBId,
                pageNo: newPageNo
            }
        }

        ZavrsniAxios.get('/utakmice', config)
            .then(res => {
                this.setState({
                    utakmice: res.data,
                    pageNo: newPageNo,
                    totalPages: res.headers['total-pages']
                });
            })
            .catch(error => {
                // handle error
                console.log(error);
                alert('Error occured please try again!');
            });
    }

    getReprezentacije() {
        ZavrsniAxios.get("/reprezentacije")
            .then((response) => {
                this.setState({ reprezentacije: response.data });
            })
            .catch((err => { console.log(err) }));
    }

    getIgraci() {
        ZavrsniAxios.get("/igraci")
            .then((response) => {
                this.setState({ igraci: response.data });
            })
            .catch((err => { console.log(err) }));
    }

    deleteFromState(utakmicaId) {
        var utakmice = this.state.utakmice;
        utakmice.forEach((element, index) => {
            if (element.id === utakmicaId) {
                utakmice.splice(index, 1);
                this.setState({ utakmice: utakmice });
                window.location.reload();
            }
        });
    }

    delete(utakmicaId) {
        ZavrsniAxios.delete('/utakmice/' + utakmicaId)
            .then(res => {
                // handle success
                console.log(res);
                alert('Utakmica was deleted successfully!');
                this.deleteFromState(utakmicaId); // ili refresh page-a window.location.reload();
            })
            .catch(error => {
                // handle error
                console.log(error);
                alert('Error occured please try again!');
            });
    }

    onInputChange(event) {
        const name = event.target.name;
        const value = event.target.value

        let search = this.state.search;
        search[name] = value;

        this.setState({ search })
    }

    goToAdd() {
        this.props.navigate('/utakmice/add');
    }

    onNumberChange(event) {
        console.log(event.target.value);

        const { name, value } = event.target;
        console.log(name + ", " + value);

        this.setState((state, props) => ({
            number: value
        }));
    }

    // buyThis(polaznik) {

    //     var params = {
    //         'id': polaznik.id,
    //         'stock': parseInt(polaznik.stock) - parseInt(this.state.number),
    //         'name': polaznik.name,
    //         'year': polaznik.year,
    //         'autoskolaId': polaznik.autoskolaId,
    //     }


    //     console.log(params)

    //     ZavrsniAxios.put("/polaznici/" + polaznik.id, params)
    //         .then((res) => {
    //             this.getWines(0)
    //             alert("Uspesno obavljena kupovina")
