    addToReprezentacijaA(utakmica) {
        var params = {
            'id': utakmica.id,
            'reprezentacijaAId': this.state.utakmice.reprezentacijaA.id,
            'reprezentacijaBId': this.state.utakmice.reprezentacijaB.id,
            'brojGolovaA': this.state.utakmice.brojGolovaA + 1,
            'brojGolovaB': this.state.utakmice.brojGolovaB
        }

        console.log(params)

        ZavrsniAxios.put("/utakmice/" + utakmica.id, params)
            .then((res) => {
                this.getUtakmice()
                this.props.navigate("/utakmice")
                window.location.reload(true);
            })
            .catch((err => {
                alert("Neuspesna nabavka")
                this.props.navigate("/utakmice")
                console.log(err)
            }));



    }
    addToReprezentacijaB(utakmica) {
        var params = {
            'id': utakmica.id,
            'reprezentacijaAId': utakmica.reprezentacijaA.id,
            'reprezentacijaBId': utakmica.reprezentacijaB.id,
            'brojGolovaA': utakmica.reprezentacijaA.id,
            'brojGolovaB': utakmica.reprezentacijaB.id + 1
        }


        console.log(params)

        ZavrsniAxios.put("/utakmice/" + utakmica.id, params)
            .then((res) => {
                this.getUtakmice()
                this.props.navigate(this.renderNajboljiStrelac)
                window.location.reload(true);
            })
            .catch((err => {
                alert("Neuspesna nabavka")
                this.props.navigate("/utakmice")
                console.log(err)
            }));



    }

    renderNajboljiStrelac() {
        return (
            <>
                <Row>
                    <Col></Col>
                    <Col xs="12" sm="10" md="8">
                        <h1 style={{ color: "purple" }}>Dodavanje utakmice</h1>
                        <Form>

                            <Form.Group>
                                <Form.Label>Strelac gola</Form.Label>
                                <Form.Select name="igracId" onChange={(e) => this.onInputChange(e)}>
                                    <option value={null}></option>
                                    {this.state.igraci.map((igrac) => {
                                        return (
                                            <option value={igrac.id} key={igrac.id}>{igrac.ime + " " + igrac.prezime}</option>
                                        );
                                    })}
                                </Form.Select>
                            </Form.Group>

                            <Button style={{ marginTop: "25px" }} onClick={this.create}>
                                Dodajte gol
                            </Button>
                        </Form>
                    </Col>
                    <Col></Col>
                </Row>
            </>
        );
    }

    renderUtakmice() {
        return this.state.utakmice.map((utakmica) => {
            const admin = window.localStorage['role'] == 'ROLE_ADMIN';
            if (admin) {
                return (
                    <tr key={utakmica.id}>
                        <td>{utakmica.reprezentacijaA.naziv}</td>
                        <td>{utakmica.reprezentacijaB.naziv}</td>
                        <td>{utakmica.brojGolovaA}</td>
                        <td>{utakmica.brojGolovaB}</td>
                        <td><Button variant="success" onClick={() => this.addToReprezentacijaA()}>A+1</Button></td>
                        <td><Button variant="success" onClick={() => this.addToReprezentacijaB()}>B+1</Button></td>
                        <td><Button variant="danger"
                            onClick={() => this.delete(utakmica.id)}>Delete</Button></td>

                    </tr>
                )
            }
        })
    }

    renderSearchForm() {
        return (
            <>
                <Form style={{ width: "100%" }}>
                    <Row><Col>
                        <Form.Group>
                            <Form.Label>Reprezentacija A</Form.Label>
                            <Form.Control name="reprezentacijaAId" value={this.state.search.reprezentacijaAId} as="select" onChange={(e) => this.onInputChange(e)}>
                                <option value={null}></option>
                                {this.state.reprezentacije.map((reprezentacija) => {
                                    return (
                                        <option value={reprezentacija.id} key={reprezentacija.id}>{reprezentacija.naziv}</option>
                                    );
                                })}
                            </Form.Control>
                        </Form.Group>
                    </Col></Row>

                    <Row><Col>
                        <Form.Group>
                            <Form.Label>Reprezentacija B</Form.Label>
                            <Form.Control name="reprezentacijaBId" value={this.state.search.reprezentacijaBId} as="select" onChange={(e) => this.onInputChange(e)}>
                                <option value={null}></option>
                                {this.state.reprezentacije.map((reprezentacija) => {
                                    return (
                                        <option value={reprezentacija.id} key={reprezentacija.id}>{reprezentacija.naziv}</option>
                                    );
                                })}
                            </Form.Control>
                        </Form.Group>
                    </Col></Row>

                </Form>
                <Row><Col>
                    <Button className="mt-3" onClick={() => this.getUtakmice(0)}>Pretraga</Button>
                </Col></Row>
            </>
        );
    }
    render() {
        return (
            <Col>
                <Row><h1 style={{ color: "purple" }}>Utakmice</h1></Row>

                <Row>
                    {this.renderSearchForm()}
                </Row>
                <br />

                <Row>
                    <Col style={{ display: 'flex', justifyContent: 'right' }}>
                        <Button disabled={this.state.pageNo === 0}
                            onClick={() => this.getUtakmice(this.state.pageNo - 1)}
                            className="mr-3">Prev</Button>
                        <Button disabled={this.state.pageNo === this.state.totalPages - 1}
                            onClick={() => this.getUtakmice(this.state.pageNo + 1)}>Next</Button>
                    </Col>
                </Row>

                <Row><Col>
                    <Table style={{ marginTop: 5 }}>
                        <thead>
                            <tr>
                                <th>Reprezentacija A</th>
                                <th>Reprezentacija B</th>
                                <th>Broj golova A</th>
                                <th>Broj golova B</th>
                                <th></th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.renderUtakmice()}
                        </tbody>
                    </Table>
                </Col></Row>

            </Col>
        );
    }

}

export default Class
